import React, { useEffect, useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

import "./Styles/allergens_form.css";

function ChangeAllergy() {
    const [formData, setFormData] = useState({
        allergies: []
    });

    useEffect(() => {
        console.log("FormData:", formData);
    }, [formData]);

    const submitForm = () => {
        const token = sessionStorage.getItem('token');
        const username = sessionStorage.getItem('username');

        axios.post('http://localhost:8080/api/user/changeAllergy', {
            name: username,
            allergies: formData.allergies,
        }, {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
            .then(response => {
                console.log("Response:", response.data);
                alert("Data submitted successfully!");
            })
            .catch(error => {
                console.error('There was an error submitting the form:', error);
                alert("Error submitting the form. Please try again later.");
            });
    };

    const updateText = () => {
        const checkbox = document.getElementById("allergyCheckbox");
        const text = document.getElementById("displayText");
        const allergies = [...formData.allergies];

        if (checkbox.checked) {
            allergies.push(text.value);
        } else {
            const index = allergies.indexOf(text.value);
            if (index !== -1) {
                allergies.splice(index, 1);
            }
        }

        setFormData(prevState => ({
            ...prevState,
            allergies: allergies
        }));
    };

    return (
        <div className="App">
            <div className="page-container">
                <div className="row">
                    <div className="col-12">
                        <form className="allergies">
                            <h3 className="text-dark text-center">Allergies</h3>
                            <div className="form-group">
                                <label htmlFor="uncommon_allergies" className="text-secondary">Select uncommon allergies:</label>
                                <select className="form-control" id="uncommon_allergies" name="uncommon_allergies" placeholder="Choose">
                                    <option value="">Choose</option>
                                    <option value="red-meat">Red meat allergy</option>
                                </select>
                            </div>
                            <div className="form-group">
                                <label htmlFor="allergyCheckbox" className="text-secondary">Choose an allergy:</label>
                                <input type="checkbox" id="allergyCheckbox" onChange={updateText} />
                                <input type="text" className="form-control" placeholder="Type allergy" id="displayText" />
                            </div>
                            <button type="button" className="btn btn-dark" onClick={submitForm}>Submit</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default ChangeAllergy;
