import React, { useEffect, useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

import "./Styles/allergens_form.css";

function User() {
    const [formData, setFormData] = useState({
        common_allergies: "",
        uncommon_allergies: "",
        other_allergies: "",
    });

    useEffect(() => {
        console.log("FormData:", formData);
    }, [formData]);

    const submitForm = () => {
        const token = sessionStorage.getItem('token');
        const username = sessionStorage.getItem('username');

        axios.put('http://localhost:8080/api/user/allergy', {
            common_allergies: formData.common_allergies,
            uncommon_allergies: formData.uncommon_allergies,
            other_allergies: formData.other_allergies,
        }, {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
            .then(response => {
                console.log("Response:", response.data);
                alert("Data updated successfully!");
            })
            .catch(error => {
                console.error('Error updating allergies:', error);
                alert("Error updating allergies. Please try again later.");
            });
    };

    const handleInputChange = (event) => {
        const { name, value } = event.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    return (
        <div className="App">
            <div className="page-container">
                <div className="row">
                    <div className="col-12">
                        <form action="/action_page.php" className="allergies">
                            <h3 className="text-dark text-center">Allergies</h3>
                            <label htmlFor="common_allergies" className="text-secondary">Most common Allergies</label>
                            <select className="form-control" id="common_allergies" name="common_allergies" value={formData.common_allergies} onChange={handleInputChange}>
                                <option value="">Choose</option>
                                <option value="peanuts">Peanuts</option>
                                <option value="tree_nuts">Tree nuts (e.g., almonds, walnuts, cashews)</option>
                                <option value="milk">Milk</option>
                            </select>
                            <br />

                            <label htmlFor="uncommon_allergies" className="text-secondary">Select uncommon allergies:</label>
                            <select className="form-control" id="uncommon_allergies" name="uncommon_allergies" value={formData.uncommon_allergies} onChange={handleInputChange}>
                                <option value="">Choose</option>
                                <option value="red-meat">Red meat allergy</option>
                            </select>
                            <br />

                            <label htmlFor="other_allergies" className="text-secondary">Other allergies:</label>
                            <input type="text" className="form-control" id="other_allergies" name="other_allergies" value={formData.other_allergies} onChange={handleInputChange} />
                            <br />

                            <button type="button" className="custom-btn btn-4 mt-3 btn btn-dark" onClick={submitForm}>Submit</button>
                        </form>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default User;
