import { useEffect, useState } from "react";
import axios from "axios";

function ChangeAllergy() {
    const [formData, setFormData] = useState({
        username: "",
        common_allergies: "",
        uncommon_allergies: "",
        other: "",
    });

    useEffect(() => {
        console.log("FormData:", formData);
    }, [formData]);

    const submitForm = () => {
        const selectedOption = document.getElementById("common_allergies").value;
        const selectedOption2 = document.getElementById("uncommon_allergies").value;
        const otherText = document.getElementById("displayText").value;

        const token = sessionStorage.getItem("token");

        setFormData((prevState) => ({
            ...prevState,
            username: "Amir",
            common_allergies: selectedOption,
            uncommon_allergies: selectedOption2,
            other: otherText,
        }));

        axios
            .put(
                "http://localhost:8081/api/users",
                {
                    username: "Amir",
                    common_allergies: selectedOption,
                    uncommon_allergies: selectedOption2,
                    other: otherText,
                },
                {
                    headers: {
                        Authorization: token,
                    },
                }
            )
            .then((response) => {
                console.log("Response:", response.data);
                alert("Allergy updated successfully!");
            })
            .catch((error) => {
                console.error("There was an error updating the allergy:", error);
                alert("Error updating the allergy. Please try again later.");
            });
    };

    const updateText = () => {
        const checkbox = document.getElementById("allergyCheckbox");
        const text = document.getElementById("displayText");
        text.style.display = checkbox.checked ? "block" : "none";
    };

    return (
        <div className="App">
            <div className="page-container">
                <div className="row">
                    <div className="col-4"></div>
                    <div className="col-4">
                        <form action="/action_page.php" className="allergies">
                            <h3 className="text-dark text-center">Change Allergy</h3>
                            <label htmlFor="common_allergies" className="text-secondary">
                                Most common Allergies
                            </label>
                            <select
                                className="form-control"
                                id="common_allergies"
                                name="common_allergies"
                                placeholder="Choose"
                            >
                                {/* options */}
                            </select>
                            <br />

                            <label htmlFor="uncommon_allergies" className="text-secondary">
                                Select uncommon allergies:
                            </label>
                            <select
                                className="form-control"
                                id="uncommon_allergies"
                                name="uncommon_allergies"
                                placeholder="Choose"
                            >
                                {/* options */}
                            </select>
                            <br />
                            <div className="ko">
                                <label htmlFor="allergyCheckbox" className="text-secondary">
                                    Choose an allergy:
                                </label>
                                <input
                                    type="checkbox"
                                    id="allergyCheckbox"
                                    onChange={updateText}
                                />
                                <input
                                    type="text"
                                    className="form-control inp"
                                    placeholder="Type allergy"
                                    id="displayText"
                                />
                            </div>
                            <button
                                type="button"
                                className="custom-btn btn-4 mt-3"
                                onClick={submitForm}
                            >
                                Update Allergy
                            </button>
                        </form>
                    </div>
                    <div className="col-4"></div>
                </div>
            </div>
        </div>
    );
}

export default ChangeAllergy;
