import React, {useEffect, useState} from "react";  // Importing React and useState hook.
import './Styles/login.css'  // Importing style file.
import axios from "axios";  // Importing axios to make REST API calls.
import {handleConfirmPasswordChange, handlePasswordChange} from "../utilities/passwordHandler";
import {useOutletContext} from "react-router-dom";  // Importing password handlers.

export default function AccountManagement() {
    // Setting up states for various form fields.
    const [newUsername, setNewUsername] = useState('');
    const [oldPassword, setOldPassword] = useState('');
    const [newEmail, setNewEmail] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmNewPassword, setConfirmNewPassword] = useState('')
    let [errors, setErrors] = useState([]);
    const [loggedInUser, setLoggedInUser] = useOutletContext();
    const [passwordMatch, setPasswordMatch] = useState(false);
    const [passwordValid, setPasswordValid] = useState(false);
    const [isButtonClicked, setIsButtonClicked] = useState(false);
    const [userID, setUserID] = useState('')
    let [title, setTitle] = useState("Account Settings");
    let [currentState, setCurrentState] = useState('');

    // Runs on page load, only runs when a user is logged in
    useEffect(() => {
        if (loggedInUser !== '') {
            const token = sessionStorage.getItem('token');
            console.log(token);
            axios({
                method: "GET",
                url: 'http://localhost:8080/api/user/findByEmail',
                params: {email: loggedInUser},
                headers: {'Authorization': token}
            }).then((response) => {
                if (response.status === 200) {
                    console.log(response.data);
                    console.log(response.data.id);
                    setUserID(response.data.id);
                }
            }).catch(error => {
                console.log(error.response);
                setUserID('Data Failure');
            })
        }
    }, [loggedInUser]);


    // Function to handle API calls to the backend server based on user's selection in the form.
    const handleBackendCall = async () => {
        const backendURL = 'http://localhost:8080/api/user';
        let data = {};
        let backendRoute = '';

        // Switch between states based on user's selection in the form.
        // TODO CHECK ENDPOINTS
        switch (currentState) {
            case 'changeEmail':
                data = {newEmail, oldPassword};
                backendRoute = '/email';
                break;
            case 'changePassword':
                if (passwordValid === false) {
                    setErrors(['New password is not valid.']);
                    setNewPassword('');
                    return;
                }
                data = {newPassword, oldPassword};
                backendRoute = '/password';
                break;
            case 'changeUsername':
                data = {newUsername, oldPassword}
                backendRoute = '/username'
                break;
            default:
                return;
        }
        let url = backendURL + backendRoute;

        // Axios call to the backend server with user's new details, along with the logged in user's token.
        axios({
            method: 'PUT',
            url: url,
            headers: {Authorization: sessionStorage.getItem("token")},
            data: data
        })
            .then(response => {
                // Check for various states after a successful call to the backend server.
                console.log(response);
                if (response.status === 200) {
                    switch (currentState) {
                        case 'changeEmail':
                            setErrors(['Email Changed Successfully!']);
                            break;
                        case 'changePassword':
                            setErrors(['Password Changed Successfully!']);
                            break;
                        case 'changeUsername':
                            setErrors(['Username Changed Successfully!']);
                            break;
                        default:
                            return;
                    }
                    setOldPassword('');
                    setConfirmNewPassword('');
                    setNewEmail('');
                    setNewPassword('');
                    setNewUsername('');
                } else {
                    // Display error message if operation failed.
                    alert('Operation failed!');
                    setErrors(['Operation ' + currentState + 'failed']);
                    setOldPassword('');
                }
            }).catch(error => {
            if (error.response) {
                let errorMessage = error.response.data;
                console.log(errorMessage);
                setErrors(['ERROR:/n' + errorMessage]);
            }
        });


    }
    // Handlers for each of the form field changes.
    const handleNewEmail = (e) => {
        setNewEmail(e.target.value);
    }
    const handleOldPassword = (op) => {
        setOldPassword(op.target.value);
    }
    const handleNewUsername = (u) => {
        setNewUsername(u.target.value);
    }
    const onChangeNewPassword = (event) => {
        const {password, isValidPassword, errors} = handlePasswordChange(event, currentState);
        setNewPassword(password);
        setErrors(errors);
        setPasswordValid(isValidPassword);
    }

    const onChangeConfirmNewPassword = (event) => {
        const {confPassword, passwordMatch, errors} = handleConfirmPasswordChange(event, currentState, newPassword)
        setConfirmNewPassword(confPassword);
        setErrors(errors);
        setPasswordMatch(passwordMatch)
    }
    // Handlers for each of the form field buttons.
    const handleEmailButton = (eb) => {
        setCurrentState("changeEmail");
        setIsButtonClicked(true);
    }

    const handleUsernameButton = (ub) => {
        setCurrentState("changeUsername");
        setIsButtonClicked(true);
    }

    const handlePasswordButton = (pb) => {
        setCurrentState("changePassword");
        setIsButtonClicked(true);
    }
    const handleAllergenButton = (ab) => {

    }

    return (
        <div className={"container"}>
            <div className="form-box" id={"form-box"}>
                <h1 id={"title"}>Account Settings</h1>
                {/*SELECTOR*/}
                {!isButtonClicked && (<div>
                    <h2 id={"sub-header"}>Select an option</h2>

                    <div className={"button-field"}>
                        <div className={"button-pair"}>
                            {/*EMAIL*/}
                            <label htmlFor={"change-email"} hidden>Change User's Email</label>
                            <button type={"button"} id={"change-email"} name={"change-email"}
                                    onClick={handleEmailButton} className={'disable'}>
                                Change Email
                            </button>
                        </div>

                        <div className={"button-pair"}>
                            {/*USERNAME*/}
                            <label htmlFor={"change-username"} hidden>Change User's Username</label>
                            <button type={"button"} id={"change-username"} name={"change-username"}
                                    onClick={handleUsernameButton} className={'disable'}>
                                Change Username
                            </button>
                        </div>

                        <div className={"button-pair"}>
                            {/*PASSWORD*/}
                            <label htmlFor={"change-password"} hidden>Change User's Password</label>
                            <button type={"button"} id={"change-password"} name={"change-password"}
                                    onClick={handlePasswordButton} className={'disable'}>
                                Change Password
                            </button>
                        </div>

                        <div className={"button-pair"}>
                            {/*ALLERGEN*/}
                            <label htmlFor={"allergen-form"} hidden>Change User's Allergens</label>
                            <button type={"button"} id={"allergen-form"} name={"allergen-form"}
                                    onClick={handleAllergenButton}
                                    className={'disable'}>{/*TODO WORK ON ALLERGEN FORM HANDLING - FOR AMIR TASK*/}
                                Change Allergens
                            </button>
                        </div>
                    </div>
                </div>)}
                <div>
                    {(isButtonClicked && currentState === 'changeEmail') && (<div>
                        <h2 id={"sub-header"}> Change Email </h2>
                        <br/>
                        <h3>New Email</h3>
                        <div className="input-field" id="email-field">
                            <label htmlFor="new-email" hidden>Email</label>
                            <input id="email" value={newEmail} name="new-email" placeholder="New Email" type="email"
                                   onChange={handleNewEmail} required={true}/>
                        </div>
                    </div>)}
                    {(isButtonClicked && currentState === 'changeUsername') && (<div>
                        <h2 id={"sub-header"}> Change Username </h2>
                        <br/>
                        <h3>New Username</h3>
                        <div className="input-field" id="username-field">
                            <label htmlFor="new-username" hidden>Username</label>
                            <input id="username" value={newUsername} name="new-username" placeholder="New Username"
                                   type="text"
                                   onChange={handleNewUsername} required={true}/>
                        </div>
                    </div>)}
                    {(isButtonClicked && currentState === 'changePassword') && (<div>
                        <h2 id={"sub-header"}> Change Password </h2>
                        <br/>
                        <h3>New Password</h3>
                        <div className="input-field" id="password-field">
                            <label htmlFor="new-password" hidden>Password</label>
                            <input id="new-password" type={"password"} value={newPassword} placeholder="New Password"
                                   onChange={onChangeNewPassword}
                                   required={true}/>
                        </div>
                        <h3>Confirm New Password</h3>
                        <div className="input-field" id="password-field">
                            <label htmlFor="confirm-new-password" hidden>Password</label>
                            <input id="confirm-new-password" type={"password"} value={confirmNewPassword}
                                   placeholder="New Password"
                                   onChange={onChangeConfirmNewPassword}
                                   required={true}/>
                        </div>
                    </div>)}
                </div>
                {isButtonClicked && (<div>
                    <h3>Current Password</h3>
                    <div className="input-field" id="password-field">
                        <label htmlFor="old-password" hidden>Password</label>
                        <input id="old-password" type={"password"} value={oldPassword} placeholder="Current Password"
                               onChange={handleOldPassword}
                               required={true}/>
                    </div>
                    <div className={"message"} id={"message"}>
                        {errors.map((error, index) => (
                            <div className="error-message" key={index}><b>{error}</b></div>))}
                    </div>
                    <div className={"button-field"}> {/*SUBMIT BUTTON*/}
                        <div className={"button-pair"}>
                            <label htmlFor={"submit"} hidden>Submit</label>
                            <button type={"button"} id={"submit"}
                                    name={"submit"}  /*TODO WORK ON FORM SUBMISSION LOGIC*/
                                    onClick={null}>
                                Change Password
                            </button>
                        </div>
                    </div>
                </div>)}
            </div>
        </div>
    );
}
