import './Styles/style.css'

function Login() {

    return (

        <div className={"container"}>
            <div className="form-box">
                <h1 id="title">Sign Up</h1>
                <form>
                    <div className="input-group" id="forms">

                        <div className="input-field" id="email-field">
                            <label htmlFor="email" hidden>Email</label>
                            <i className="fa-solid fa-envelope"></i>
                            <input id="email" name="email" placeholder="Email" type="email"></input>
                        </div>

                        <div className="input-field" id="username-field">
                            <label htmlFor="username" hidden>Username</label>
                            <i className="fa-solid fa-user"></i>
                            <input id="username" name="username" placeholder="Username" type="text"></input>
                        </div>

                        <div className="input-field" id="password-field">
                            <label htmlFor="password" hidden>Password</label>
                            <i className="fa-solid fa-lock"></i>
                            <input id="password" name="password" placeholder="Password" type="password"></input>
                        </div>

                        <div className="input-field" id="confirm-password-field">
                            <label htmlFor="confirm-password" hidden>Confirm Password</label>
                            <i className="fa-solid fa-lock"></i>
                            <input id="confirm-password" name="confirm-password" placeholder="Confirm Password"
                                   type="password"></input>
                        </div>

                    </div>

                    <div className="button-field">
                        <label htmlFor={"sign-up"} hidden>Sign Up Button</label>
                        <button type="button" id="sign-up" name="sign-up" onClick={btnSignUpClick}>Sign Up</button>
                        <label htmlFor={"sign-in"} hidden>Sign In Button</label>
                        <button type="button" id="sign-in" name="sign-in" className="disable"
                                onClick={btnSignInClick}>Sign In
                        </button>


                    </div>

                    <div className="button-field">
                        <label htmlFor={"recovery"} hidden>Recover Account Button</label>
                        <button type={"button"} className={"disable"} id={"recovery"} name={"recovery"}
                                onClick={btnRecoveryClick}>Recover Account
                        </button>
                    </div>

                </form>

            </div>
        </div>
    );


}


function btnSignUpClick() {
    const btnSignUp = document.getElementById("sign-up");
    const btnSignIn = document.getElementById("sign-in");
    const btnRecover = document.getElementById("recovery");
    const fieldEmail = document.getElementById("email-field");
    const fieldUsername = document.getElementById("username-field");
    const fieldPassword = document.getElementById("password-field");
    const fieldConfirmPassword = document.getElementById("confirm-password-field");
    const title = document.getElementById("title");
    const form = document.getElementById("forms");
    if (btnSignUp.classList.contains("disable")) {
        // Expand necessary fields
        fieldEmail.style.maxHeight = "65px";
        fieldConfirmPassword.style.maxHeight = "65px";
        fieldUsername.style.maxHeight = "65px";
        fieldPassword.style.maxHeight = "65px";

        // Shrink Form to size
        form.style.height = "320px";

        //Change Page Title
        title.innerHTML = "Sign Up";

        //Set Disabled Class
        btnRecover.classList.add("disable");
        btnSignIn.classList.add("disable");
        btnSignUp.classList.remove("disable");
    } else {
        //     Code for checking fields and stuff here
    }
}

function btnSignInClick() {
    const btnSignUp = document.getElementById("sign-up");
    const btnSignIn = document.getElementById("sign-in");
    const btnRecover = document.getElementById("recovery");
    const fieldEmail = document.getElementById("email-field");
    const fieldUsername = document.getElementById("username-field");
    const fieldPassword = document.getElementById("password-field");
    const fieldConfirmPassword = document.getElementById("confirm-password-field");
    const title = document.getElementById("title");
    const form = document.getElementById("forms");
    if (btnSignIn.classList.contains("disable")) {


        fieldEmail.hidden = true;
        fieldConfirmPassword.hidden = true;
        fieldEmail.style.maxHeight = "0";
        fieldConfirmPassword.style.maxHeight = "0";

        fieldUsername.style.maxHeight = "65px";
        fieldPassword.style.maxHeight = "65px";

        form.style.height = "200px";

        title.innerHTML = "Sign In";


        btnSignUp.classList.add("disable");
        btnRecover.classList.add("disable");
        btnSignIn.classList.remove("disable");
    }
}

function btnRecoveryClick() {
    const btnSignUp = document.getElementById("sign-up");
    const btnSignIn = document.getElementById("sign-in");
    const btnRecover = document.getElementById("recovery");
    const fieldEmail = document.getElementById("email-field");
    const fieldUsername = document.getElementById("username-field");
    const fieldPassword = document.getElementById("password-field");
    const fieldConfirmPassword = document.getElementById("confirm-password-field");
    const title = document.getElementById("title");
    const form = document.getElementById("forms");
    if (btnRecover.classList.contains("disable")) {
        // Shrink Unnecessary fields
        fieldPassword.style.maxHeight = "0";
        fieldUsername.style.maxHeight = "0";
        fieldConfirmPassword.style.maxHeight = "0";

        // Expand necessary fields
        fieldEmail.style.maxHeight = "65px";

        // Shrink Form to size
        form.style.height = "110px";

        //Change Page Title
        title.innerHTML = "Recover Password";

        //Set Disabled Class
        btnRecover.classList.remove("disable");
        btnSignUp.classList.add("disable");
        btnSignIn.classList.add("disable");
    }
}

export default Login;
