import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Styles/AllComments.css"
function AllComments() {
    const [comments, setComments] = useState([]);

    useEffect(() => {
        axios.get("http://localhost:8080/api/user/getAllComment")
            .then(response => {
                setComments(response.data);
            })
            .catch(error => {
                console.error('Error fetching comments:', error);
                // Handle error fetching comments
            });
    }, []);

    return (
        <div className=" page-container ">
            <h1>All Comments</h1>
            <ul className={'scroll-area'}>
                {comments.map(comment => (
                    <div className="">
                        <div className="car-item ">
                            <div className="list " key={comment.id}>
                                <strong>Name:</strong> {comment.name}<br/>
                                <strong>Username:</strong> {comment.email}<br/>
                                <strong>Feedback:</strong> {comment.feedback}
                            </div>
                        </div>
                    </div>

                ))}
            </ul>
        </div>
    );
}

export default AllComments;
