import React, { useEffect, useState } from "react";
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";

function EditAllergy() {
    const [formData, setFormData] = useState({
        common_allergies: "",
        uncommon_allergies: "",
        other_allergies: "",
    });

    useEffect(() => {
        // Fetch existing allergy data for the user
        const email = sessionStorage.getItem('username');
        axios.get(`http://localhost:8080/api/user/allergy/${email}`)
            .then(response => {
                const allergyData = response.data;
                setFormData({
                    common_allergies: allergyData.common_allergies,
                    uncommon_allergies: allergyData.uncommon_allergies,
                    other_allergies: allergyData.other_allergies,
                });
            })
            .catch(error => {
                console.error('Error fetching allergy data:', error);
            });
    }, []);

    const submitForm = () => {
        const token = sessionStorage.getItem('token');
        const email = sessionStorage.getItem('username');
        axios.put(`http://localhost:8080/api/user/allergy/${email}`, formData, {
            headers: {
                'Authorization': `Bearer ${token}`
            }
        })
            .then(response => {
                console.log("Response:", response.data);
                alert("Allergy updated successfully!");
            })
            .catch(error => {
                console.error('Error updating allergy:', error);
                alert("Failed to update allergy.");
            });
    };

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    return (
        <div className="container">
            <h3>Edit Allergy</h3>
            <form>
                <div className="form-group">
                    <label htmlFor="common_allergies">Common Allergies</label>
                    <input type="text" className="form-control" id="common_allergies" name="common_allergies" value={formData.common_allergies} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="uncommon_allergies">Uncommon Allergies</label>
                    <input type="text" className="form-control" id="uncommon_allergies" name="uncommon_allergies" value={formData.uncommon_allergies} onChange={handleChange} />
                </div>
                <div className="form-group">
                    <label htmlFor="other_allergies">Other Allergies</label>
                    <input type="text" className="form-control" id="other_allergies" name="other_allergies" value={formData.other_allergies} onChange={handleChange} />
                </div>
                <button type="button" className="btn btn-primary" onClick={submitForm}>Submit</button>
            </form>
        </div>
    );
}

export default EditAllergy;
